mapper.select:
select

from biz_item_apply
where applyid = #{applyid,jdbcType=INTEGER}

SELECT charge.price AS `price`, itemapply.applyid AS applyid, itemapply.apply_no AS applyNo, itemapply.created_date AS createdDate, itemapply.submit_date AS submitDate, itemapply.`describe` AS `describe`, itemapply.itemid AS itemid, itemapply.item_value AS itemValue, itemapply.`status` AS `status`, itemapply.`pay_status` AS `payStatus`, itemapply.userid AS userid, itemapply.add_file_status AS addFileStatus, itemapply.wf_instance_id AS wfInstanceId, orgitem.item_name AS itemName, orgitem.logo_url AS logoUrl FROM biz_item_apply AS itemapply LEFT JOIN biz_org_items AS orgitem ON itemapply.itemid = orgitem.itemid LEFT JOIN biz_charge_detail AS charge ON (charge.apply_id = itemapply.applyid AND charge.charge_id = (select id from biz_item_charge where `name` = '公证预付款' and org_id = (SELECT orgid FROM biz_org_users WHERE userid = #{userId} GROUP BY userid) and is_del = 0) ) WHERE itemapply.userid = #{userId}
AND itemapply.`status` = #{state}
AND orgitem.item_name like CONCAT('%',#{word},'%')
GROUP BY itemapply.applyid ORDER BY createdDate DESC

SELECT itemapply.applyid AS applyid, itemapply.apply_no AS applyNo, itemapply.certid AS certid, itemapply.created_date AS createdDate, itemapply.submit_date as submitDate, itemapply.`describe` AS `describe`, itemapply.itemid AS itemid, itemapply.item_value AS itemValue, itemapply.price AS price, itemapply.`status` AS `status`, itemapply.userid AS userid, itemapply.wf_instance_id AS wfInstanceId, orgitem.item_name AS itemName, usercard.real_name AS realName, orgitem.logo_url AS logoUrl FROM biz_item_apply AS itemapply LEFT JOIN biz_org_items AS orgitem ON itemapply.itemid = orgitem.itemid LEFT JOIN sys_user_card usercard ON (itemapply.userid = usercard.userid AND usercard.real_state = 2) where 1 = 1
and itemapply.itemid = #{itemId}
and itemapply.`status` = #{status} and itemapply.handle_userid = 0 and itemapply.handle_created_date is null group by itemapply.applyid order by submitDate desc

SELECT itemapply.applyid AS applyid, itemapply.apply_no AS applyNo, itemapply.certid AS certid, itemapply.created_date AS createdDate, itemapply.submit_date as submitDate, itemapply.`describe` AS `describe`, itemapply.itemid AS itemid, itemapply.item_value AS itemValue, itemapply.price AS price, itemapply.`status` AS `status`, itemapply.userid AS userid, itemapply.wf_instance_id AS wfInstanceId, orgitem.item_name AS itemName, usercard.real_name AS realName, orgitem.logo_url AS logoUrl FROM biz_item_apply AS itemapply LEFT JOIN biz_org_items AS orgitem ON itemapply.itemid = orgitem.itemid LEFT JOIN sys_user_card usercard ON (itemapply.userid = usercard.userid AND usercard.real_state = 2) where 1=1
and itemapply.itemid = #{itemId}
and itemapply.`status` in
#{state}
and itemapply.handle_userid = #{userId} order by submitDate desc

SELECT itemapply.applyid AS applyid, itemapply.apply_no AS applyNo, itemapply.submit_date AS submitDate, itemapply.`status` AS `status`, item.item_name AS itemName, item.logo_url AS logoUrl, usercard.real_name AS realName FROM biz_item_apply AS itemapply LEFT JOIN biz_org_items AS item ON itemapply.itemid = item.itemid LEFT JOIN sys_user_card AS usercard ON itemapply.userid = usercard.userid LEFT JOIN wf_item_node_define AS nodedefine ON nodedefine.apply_id = itemapply.applyid WHERE nodedefine.id IN ( SELECT wfin.nodeid FROM wf_instance AS wfin WHERE wfin.id IN ( SELECT apply.wf_instance_id FROM `biz_item_apply` AS apply WHERE apply.`status` = #{status} ) ) AND nodedefine.audit_userid = #{userId}
AND (itemapply.apply_no like CONCAT('%',#{word},'%') or item.item_name like CONCAT('%',#{word},'%'))
AND itemapply.itemid = #{itemId}
GROUP BY itemapply.applyid ORDER BY itemapply.submit_date DESC

SELECT itemapply.applyid AS applyid, itemapply.apply_no AS applyNo, itemapply.submit_date AS submitDate, itemapply.`status` AS `status`, item.item_name AS itemName, item.logo_url AS logoUrl, usercard.real_name AS realName, track.audit_date AS auditDate FROM wf_inst_audit_track AS track LEFT JOIN biz_item_apply AS itemapply ON track.instid = itemapply.wf_instance_id LEFT JOIN biz_org_items AS item ON itemapply.itemid = item.itemid LEFT JOIN sys_user_card AS usercard ON itemapply.userid = usercard.userid WHERE track.userid = #{userId}
AND (itemapply.apply_no like CONCAT('%',#{word},'%') or item.item_name like CONCAT('%',#{word},'%'))
AND itemapply.itemid = #{itemId}
GROUP BY itemapply.applyid ORDER BY track.audit_date DESC

SELECT
count(1)
FROM
biz_item_apply AS itemapply
WHERE itemapply.`status` = #{status}
AND itemapply.handle_userid = #{userId}

SELECT count(1) FROM biz_item_apply AS itemapply WHERE itemapply.`status` IN
#{end}
AND itemapply.handle_userid = #{userId}

SELECT itemapply.applyid AS applyid, itemapply.apply_no AS applyNo, itemapply.submit_date AS submitDate, itemapply.`status` AS `status`, item.item_name AS itemName, item.logo_url AS logoUrl, item.itemid AS itemid FROM biz_item_apply AS itemapply LEFT JOIN biz_org_items item ON itemapply.itemid = item.itemid WHERE itemapply.`status` IN
#{inPro}
AND itemapply.handle_userid = #{userId}

SELECT itemapply.applyid AS applyid, itemapply.apply_no AS applyNo, itemapply.submit_date AS submitDate, itemapply.`status` AS `status`, item.item_name AS itemName, item.logo_url AS logoUrl, item.itemid AS itemid, usercard.real_name AS realName FROM biz_item_apply AS itemapply LEFT JOIN biz_org_items AS item ON itemapply.itemid = item.itemid LEFT JOIN sys_user_card AS usercard ON itemapply.userid = usercard.userid WHERE itemapply.`status` IN
#{s}
AND itemapply.handle_userid = #{userId}
AND itemapply.itemid = #{itemId}
AND (itemapply.apply_no like CONCAT('%',#{word},'%') or item.item_name like CONCAT('%',#{word},'%'))
GROUP BY itemapply.applyid ORDER BY itemapply.submit_date DESC
